"use client";
import { useContainerSize } from "@/hooks/useContainerSize";
import { useAppStore } from "@/store/useAppStore";
import { getIndexContentEl } from "@/utils/dom/getContainer";
import styled from "@emotion/styled";
import { useTranslations } from "next-intl";
import * as React from "react";
import { useEffect, useRef, useState } from "react";
import { mediaMax } from "@/styles/media";
import TypingAnimation from "./TypingText";
import FadeText from "./FadeText";

interface Props {}

export function MainVisual({}: Props) {
  const setVisualHeight = useAppStore((state) => state.setVisualHeight);
  const t = useTranslations("pages.index");
  const videoRef = useRef<any>(); //Type error: 'video' is possibly 'null'. 문제 해결
  const [currentTime, setCurrentTime] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [heroTitle, setHeroTitle] = useState("");
  const [heroDesc, setHeroDesc] = useState("");
  const [titleShow, setTitleShow] = useState([false, false, false, false]);

  const containerRef = useRef<HTMLDivElement>(null);
  const { width: containerWidth, height: containerHeight } = useContainerSize(containerRef);

  useEffect(() => {
    const video = videoRef.current;
    const updateTime = () => {
      setCurrentTime(video.currentTime);
      const currentTime = videoRef.current?.currentTime;
      setSeconds(currentTime.toFixed(1));
      // console.log(seconds);
      if (seconds >= 0 && seconds < 3.1) {
        if (!titleShow[0]) {
          setHeroTitle(t("hero-title-1"));
          setHeroDesc(t("hero-title-p-1"));
          setTitleShow([true, false, false, false]);
        }
        // console.log(t('hero-title-p-1'))
      } else if (seconds >= 3.1 && seconds < 11.6) {
        if (!titleShow[1]) {
          setHeroTitle(t("hero-title-2"));
          setHeroDesc(t("hero-title-p-2"));
          setTitleShow([false, true, false, false]);
        }
      } else if (seconds >= 11.6 && seconds < 15.6) {
        if (!titleShow[2]) {
          setHeroTitle(t("hero-title-3"));
          setHeroDesc(t("hero-title-p-3"));
          setTitleShow([false, false, true, false]);
        }
      } else if (seconds >= 15.6) {
        if (!titleShow[3]) {
          setHeroTitle(t("hero-title-4"));
          setHeroDesc(t("hero-title-p-4"));
          setTitleShow([false, false, false, true]);
        }
      }
    };

    // Add event listener to track time updates
    video.addEventListener("timeupdate", updateTime);

    return () => {
      // Clean up the event listener on component unmount
      video.removeEventListener("timeupdate", updateTime);
    };
  }, [seconds, heroTitle, heroDesc, titleShow, t]);

  useEffect(() => {
    // console.debug("containerHeight", containerHeight);

    setVisualHeight(containerHeight);

    const indexContentEl = getIndexContentEl();
    if (indexContentEl) {
      indexContentEl.style.marginTop = `${containerHeight}px`;
    }
  }, [containerHeight, setVisualHeight]);

  return (
    <Div>
      <div className={"HeroWrapper"} ref={containerRef}>
        <div className={"HeroOverlay"}></div>
        <video ref={videoRef} poster='/video/ax-hero.jpg' id='background-video' playsInline autoPlay muted loop>
          <source src='/video/ax-hero.mp4' type='video/mp4' />
        </video>
        <div className={"HeroTitle"}>
          <div className={"container"}>
            <div className={"htbox"}>
              <h1>
                <TypingAnimation text={heroTitle} speed={100} />
              </h1>
              <p>
                <FadeText text={heroDesc} />
              </p>
            </div>
          </div>
        </div>
        <div className={"IconMouseScroll"}></div>
      </div>
    </Div>
  );
}

const Div = styled.div`
  .HeroWrapper{
    height:60vh;
    min-height: 500px;
    width:100vw;

    position: fixed;
    left:0;
    top:0;
    z-index: 1;
    background:#eaeaea url('/video/ax-hero.jpg') no-repeat top left;
    background-size:cover;
    overflow: hidden;

    padding-bottom: 5.5rem;
    ${mediaMax.md} {
        padding-bottom: 3rem;
    }

      video{
      display: block;
      min-width:100%;
      min-height:100%;
      width:auto;
      height:auto;
      position: absolute;
      transform:translateX(-50%) translateY(-50%);
      top:50%;
      left:50%;
      object-fit: contain;
      overflow-clip-margin: content-box;
      overflow: clip;
    }
    .HeroOverlay{
      display: block;
      position: absolute;
      min-width:100%;
      min-height:100%;
      left:0;
      top:0;
      background: var(--black);
      opacity: .05;
    }
    .HeroTitle{
      position: absolute;
      top:0;
      left:0;
      display: flex;
      justify-content: center;
      align-items: center;
      width:100%;
      height:100%;
      z-index: 3;
      .htbox{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: flex-start;
        h1{
          font-size:2rem;
          display: inline-block;
          color: var(--txt-display);
          font-family: var(--font-display);
        }
        p{
          font-size:1.125rem;
          line-height:1.5rem;
          word-break:keep-all;
          display: inline-block;
          padding:1rem 0;
          color: var(--txt-body);
          font-family: var(--font-display);
        }
      }
    }
  }
  .IconMouseScroll{
    position:absolute;
    left:calc(50% - 1.5rem);
    bottom:1.5rem;
    z-index:4;
    width: 3rem;
    height: 3rem;
    background-image:url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    background-position:center;
  }
}
${mediaMax.md}{
  .HeroWrapper {
    .HeroTitle {
      .htbox {
        padding: 0 2rem;
        h1 {
          font-size: 1.5rem !important;
        }
        p {
          font-size: 0.875rem !important;
          line-height: 1rem !important;
        }
      }
    }
  }
}
`;
